import React, { Component } from 'react';
import Header from './components/Header';
import Contents from './components/Contents';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      year: 'all',
      movies: [
        {id:1, photo:'http://kobis.or.kr/common/mast/movie/2020/02/8f7964477e814a79833c4344678556e2.jpg', title:'기생충 (2019)', year:'2019'},
