    return (
      <div className="App">
        <Header
          onChangeYear={(year) => {
            this.setState({
              year: year
            });
          }}
        ></Header>
        <Contents
          data={this.state.movies}
          selected_year={this.state.year}
        ></Contents>
      </div>
    );
  }
}

export default App;
