import React, { Component } from 'react';
import Header from './components/Header';
import Contents from './components/Contents';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      year: 'all',
      movies: [
        {id:1, photo:'http://placehold.it/150x200', title:'기생충', year:'2019'},
        {id:2, photo:'http://placehold.it/150x200', title:'나들이', year:'2018'},
        {id:3, photo:'http://placehold.it/150x200', title:'첫만남', year:'2017'},
        {id:4, photo:'http://placehold.it/150x200', title:'부산행', year:'2016'},
        {id:5, photo:'http://placehold.it/150x200', title:'블루스 이야기', year:'2015'}
      ]
    }
  }
  componentWillMount() {
    document.title = '칸 국제영화제'
  }
  render() {
    return (
      <div className="App">
        <Header
          onChangeYear={(year) => {
            this.setState({
              year: year
            });
          }}
        ></Header>
        <Contents
          data={this.state.movies}
          selected_year={this.state.year}
        ></Contents>
      </div>
    );
  }
}

export default App;
